package testingCode;import java.util.Scanner;public class CondensedVowel{public static char[] v={'a','e','i','o','u'};public static void main(String[] a){Scanner s=new Scanner(System.in);String n="";int[] c=new int[v.length];int y=0;try{System.out.print("Enter a string to check for vowels: ");n=s.nextLine().toLowerCase();}finally{s.close();}for(int i=0;i<n.length();i++){if(a(n,i)){char u=n.charAt(i);if(u !='y')c[l(v,u)]++;else++y;}}int w=y;for(int i=0;i<v.length;i++){System.out.println(v[i]+": "+c[i]);w+=c[i];}System.out.println("y: "+y+"\nspaces: "+(n.length()-n.replaceAll("\\s","").length())+"\nconsonants: "+(n.length()-n.replaceAll("[a-z]","").length()-w)+"\npunctuation: "+(n.replaceAll("[a-z0-9\\s]","").length()));}public static boolean a(String s,int x){if(x<s.length()&&x>=0){char t=s.charAt(x);if(l(v,t)>-1)return true;if(t=='y')return!a(s,x+1)&&!a(s,x-1);}return false;}public static int l(char[] q,char c){for(int i=0;i<q.length;i++)if(q[i]==c)return i;return-1;}}